import React from 'react';

const Navbar = ({ activePage, onPageChange }) => {
  const navItems = [
    { id: 'about', label: 'About' },
    { id: 'resume', label: 'Resume' },
    { id: 'portfolio', label: 'Portfolio' },
    { id: 'contact', label: 'Contact' }
  ];

  return (
    <nav className="bg-eerie-black-1 border-b border-light-gray-70 sticky top-0 z-40">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
          <div className="flex">
            <ul className="flex space-x-8">
              {navItems.map((item) => (
                <li key={item.id}>
                  <button
                    onClick={() => onPageChange(item.id)}
                    className={`
                      inline-flex items-center px-1 pt-1 text-sm font-medium
                      transition-colors duration-200
                      ${activePage === item.id
                        ? 'text-orange-yellow border-b-2 border-orange-yellow'
                        : 'text-light-gray hover:text-white-1 hover:border-b-2 hover:border-light-gray'
                      }
                    `}
                  >
                    {item.label}
                  </button>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
