import React, { useState } from 'react';
// Option: Replace with react-icons if lucide-react causes issues
// import { IoSend, IoMail, IoPerson, IoChatbubble, IoCheckmarkCircle, IoAlertCircle } from 'react-icons/io5';
// import { AiOutlineLoading3Quarters } from 'react-icons/ai';

import { Send, Mail, User, MessageSquare, CheckCircle, AlertCircle, Loader } from 'lucide-react';

const Contact = () => {
  const [formData, setFormData] = useState({
    fullname: '',
    email: '',
    subject: '',
    message: ''
  });
  const [isFormValid, setIsFormValid] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState(null); // 'success', 'error', null
  const [errors, setErrors] = useState({});

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validateForm = (data) => {
    const newErrors = {};
    
    if (!data.fullname.trim()) {
      newErrors.fullname = 'Full name is required';
    } else if (data.fullname.trim().length < 2) {
      newErrors.fullname = 'Name must be at least 2 characters';
    }
    
    if (!data.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!validateEmail(data.email)) {
      newErrors.email = 'Please enter a valid email address';
    }
    
    if (!data.subject.trim()) {
      newErrors.subject = 'Subject is required';
    }
    
    if (!data.message.trim()) {
      newErrors.message = 'Message is required';
    } else if (data.message.trim().length < 10) {
      newErrors.message = 'Message must be at least 10 characters';
    }
    
    return newErrors;
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear specific error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }

    // Check form validation
    const newFormData = { ...formData, [name]: value };
    const formErrors = validateForm(newFormData);
    setIsFormValid(Object.keys(formErrors).length === 0);
    setSubmitStatus(null);
  };

  const sendEmail = async (data) => {
    // Method 1: Formspree (Recommended - 1000 free submissions/month)
    // Replace 'YOUR_FORM_ID' with your actual Formspree form ID
    try {
      const response = await fetch('https://formspree.io/f/YOUR_FORM_ID', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: data.fullname,
          email: data.email,
          subject: data.subject,
          message: data.message,
          _replyto: data.email, // This will set the reply-to address
          _subject: `Contact Form: ${data.subject}`, // This will be the email subject
        }),
      });
      
      if (!response.ok) {
        throw new Error('Failed to send message');
      }
      
      return response.json();
    } catch (error) {
      throw new Error('Network error: ' + error.message);
    }

    // Method 2: FormSubmit (Alternative - Unlimited submissions)
    // Uncomment below and comment above for FormSubmit
    /*
    try {
      const response = await fetch('https://formsubmit.co/tusharjaninqwerty@gmail.com', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: data.fullname,
          email: data.email,
          subject: data.subject,
          message: data.message,
        }),
      });
      
      if (!response.ok) {
        throw new Error('Failed to send message');
      }
      
      return response.json();
    } catch (error) {
      throw new Error('Network error: ' + error.message);
    }
    */
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const formErrors = validateForm(formData);
    setErrors(formErrors);
    
    if (Object.keys(formErrors).length === 0) {
      setIsSubmitting(true);
      setSubmitStatus(null);
      
      try {
        await sendEmail(formData);
        setSubmitStatus('success');
        setFormData({ fullname: '', email: '', subject: '', message: '' });
        setIsFormValid(false);
      } catch (error) {
        setSubmitStatus('error');
        console.error('Error sending email:', error);
      } finally {
        setIsSubmitting(false);
      }
    }
  };

  const inputClasses = (fieldName) => `
    w-full px-4 py-3 bg-gray-800 border-2 rounded-lg transition-all duration-300 text-white placeholder-gray-400
    focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent
    ${errors[fieldName] 
      ? 'border-red-500 focus:ring-red-500' 
      : 'border-gray-600 hover:border-gray-500'
    }
  `;

  return (
    <div className="max-w-4xl mx-auto p-6 bg-gray-900 min-h-screen">
      {/* Header */}
      <div className="text-center mb-12">
        <h2 className="text-4xl font-bold text-white mb-4 bg-gradient-to-r from-blue-400 to-purple-500 bg-clip-text text-transparent">
          Get In Touch
        </h2>
        <p className="text-gray-300 text-lg max-w-2xl mx-auto">
          Have a question or want to work together? I'd love to hear from you. 
          Send me a message and I'll respond as soon as possible.
        </p>
      </div>

      {/* Contact Form Card */}
      <div className="bg-gray-800 rounded-2xl p-8 shadow-2xl border border-gray-700">
        <div className="space-y-6">
          {/* Name and Email Row */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-2">
              <label className="flex items-center text-sm font-medium text-gray-300 mb-2">
                <IoPerson className="w-4 h-4 mr-2 text-blue-400" />
                Full Name
              </label>
              <input
                type="text"
                name="fullname"
                value={formData.fullname}
                onChange={handleInputChange}
                placeholder="Enter your full name"
                className={inputClasses('fullname')}
              />
              {errors.fullname && (
                <p className="text-red-400 text-sm flex items-center mt-1">
                  <IoAlertCircle className="w-4 h-4 mr-1" />
                  {errors.fullname}
                </p>
              )}
            </div>

            <div className="space-y-2">
              <label className="flex items-center text-sm font-medium text-gray-300 mb-2">
                <IoMail className="w-4 h-4 mr-2 text-blue-400" />
                Email Address
              </label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                placeholder="Enter your email address"
                className={inputClasses('email')}
              />
              {errors.email && (
                <p className="text-red-400 text-sm flex items-center mt-1">
                  <IoAlertCircle className="w-4 h-4 mr-1" />
                  {errors.email}
                </p>
              )}
            </div>
          </div>

          {/* Subject */}
          <div className="space-y-2">
            <label className="flex items-center text-sm font-medium text-gray-300 mb-2">
              <IoChatbubble className="w-4 h-4 mr-2 text-blue-400" />
              Subject
            </label>
            <input
              type="text"
              name="subject"
              value={formData.subject}
              onChange={handleInputChange}
              placeholder="What's this about?"
              className={inputClasses('subject')}
            />
            {errors.subject && (
              <p className="text-red-400 text-sm flex items-center mt-1">
                <IoAlertCircle className="w-4 h-4 mr-1" />
                {errors.subject}
              </p>
            )}
          </div>

          {/* Message */}
          <div className="space-y-2">
            <label className="flex items-center text-sm font-medium text-gray-300 mb-2">
              <IoChatbubble className="w-4 h-4 mr-2 text-blue-400" />
              Message
            </label>
            <textarea
              name="message"
              value={formData.message}
              onChange={handleInputChange}
              placeholder="Tell me about your project, questions, or just say hello..."
              rows="6"
              className={`${inputClasses('message')} resize-none`}
            />
            {errors.message && (
              <p className="text-red-400 text-sm flex items-center mt-1">
                <IoAlertCircle className="w-4 h-4 mr-1" />
                {errors.message}
              </p>
            )}
            <p className="text-gray-400 text-sm">
              {formData.message.length}/500 characters
            </p>
          </div>

          {/* Status Messages */}
          {submitStatus === 'success' && (
            <div className="bg-green-900/50 border border-green-500 rounded-lg p-4 flex items-center">
              <IoCheckmarkCircle className="w-5 h-5 mr-3 text-green-400" />
              <div>
                <p className="text-green-400 font-medium">Message sent successfully!</p>
                <p className="text-green-300 text-sm">Thank you for reaching out. I'll get back to you soon.</p>
              </div>
            </div>
          )}

          {submitStatus === 'error' && (
            <div className="bg-red-900/50 border border-red-500 rounded-lg p-4 flex items-center">
              <IoAlertCircle className="w-5 h-5 mr-3 text-red-400" />
              <div>
                <p className="text-red-400 font-medium">Failed to send message</p>
                <p className="text-red-300 text-sm">Please try again or contact me directly.</p>
              </div>
            </div>
          )}

          {/* Submit Button */}
          <button
            type="button"
            onClick={handleSubmit}
            disabled={!isFormValid || isSubmitting}
            className={`
              w-full py-4 px-6 rounded-lg font-medium text-white transition-all duration-300 
              flex items-center justify-center space-x-2 text-lg
              ${isFormValid && !isSubmitting
                ? 'bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 transform hover:scale-[1.02] shadow-lg hover:shadow-xl'
                : 'bg-gray-600 cursor-not-allowed opacity-50'
              }
            `}
          >
            {isSubmitting ? (
              <>
                <AiOutlineLoading3Quarters className="w-5 h-5 animate-spin" />
                <span>Sending Message...</span>
              </>
            ) : (
              <>
                <IoSend className="w-5 h-5" />
                <span>Send Message</span>
              </>
            )}
          </button>

          {/* Contact Info */}
          <div className="mt-8 pt-6 border-t border-gray-700 text-center">
            <p className="text-gray-400 mb-2">Or reach out directly:</p>
            <a 
              href="mailto:tusharjaninqwerty@gmail.com" 
              className="text-blue-400 hover:text-blue-300 transition-colors duration-200 flex items-center justify-center space-x-2"
            >
              <IoMail className="w-4 h-4" />
              <span>tusharjaninqwerty@gmail.com</span>
            </a>
          </div>
        </div>
      </div>

      {/* Email Integration Options */}
      <div className="mt-8 space-y-4">
        {/* Free Alternative: Formspree */}
        <div className="p-6 bg-green-900/20 border border-green-500/30 rounded-lg">
          <h3 className="text-white font-semibold mb-3 flex items-center">
            <IoCheckmarkCircle className="w-5 h-5 mr-2 text-green-400" />
            Recommended: Formspree (1000 free submissions/month)
          </h3>
          <div className="text-gray-300 text-sm space-y-2">
            <p>Best free option with generous limits:</p>
            <ol className="list-decimal ml-4 space-y-1">
              <li>Sign up at <span className="text-green-400">formspree.io</span></li>
              <li>Create a new form and get your form ID</li>
              <li>Update form action: <code className="bg-gray-800 px-2 py-1 rounded text-green-300">action="https://formspree.io/f/YOUR_FORM_ID"</code></li>
              <li>No JavaScript required - works with simple HTML forms!</li>
            </ol>
          </div>
        </div>

        {/* Alternative: Netlify Forms */}
        <div className="p-6 bg-purple-900/20 border border-purple-500/30 rounded-lg">
          <h3 className="text-white font-semibold mb-3 flex items-center">
            <IoMail className="w-5 h-5 mr-2 text-purple-400" />
            Alternative: Netlify Forms (100 free submissions/month)
          </h3>
          <div className="text-gray-300 text-sm space-y-2">
            <p>Perfect if you're hosting on Netlify:</p>
            <ol className="list-decimal ml-4 space-y-1">
              <li>Add <code className="bg-gray-800 px-2 py-1 rounded text-purple-300">data-netlify="true"</code> to your form</li>
              <li>Deploy to Netlify - forms are automatically handled</li>
              <li>Get email notifications for each submission</li>
            </ol>
          </div>
        </div>

        {/* Other Options */}
        <div className="p-6 bg-blue-900/20 border border-blue-500/30 rounded-lg">
          <h3 className="text-white font-semibold mb-3 flex items-center">
            <IoChatbubble className="w-5 h-5 mr-2 text-blue-400" />
            Other Free Options
          </h3>
          <div className="text-gray-300 text-sm space-y-2">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <p className="font-medium text-white mb-1">Web3Forms</p>
                <p>1000 submissions/month</p>
                <p className="text-blue-400">web3forms.com</p>
              </div>
              <div>
                <p className="font-medium text-white mb-1">FormSubmit</p>
                <p>Unlimited submissions</p>
                <p className="text-blue-400">formsubmit.co</p>
              </div>
              <div>
                <p className="font-medium text-white mb-1">EmailJS</p>
                <p>200 submissions/month</p>
                <p className="text-blue-400">emailjs.com</p>
              </div>
              <div>
                <p className="font-medium text-white mb-1">99inbound</p>
                <p>100 submissions/month</p>
                <p className="text-blue-400">99inbound.com</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Contact;
